          start = end = i + 1
        }
        break
      case 0x2c: /* , */
        list.push(header.substring(start, end))
        start = end = i + 1
        break
      default:
        end = i + 1
        break
    }
  }

  // final token
  list.push(header.substring(start, end))

  return list
}

/**
 * Mark that a request is varied on a header field.
 *
 * @param {Object} res
 * @param {String|Array} field
 * @public
 */

function vary (res, field) {
  if (!res || !res.getHeader || !res.setHeader) {
    // quack quack
    throw new TypeError('res argument is required')
  }

  // get existing header
  var val = res.getHeader('Vary') || ''
  var header = Array.isArray(val)
    ? val.join(', ')
    : String(val)

  // set new header
  if ((val = append(header, field))) {
    res.setHeader('Vary', val)
  }
}

// tools/monitor/performance.js
const puppeteer = require('puppeteer');
const fs = require('fs').promises;

async function monitorPerformance() {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    
    // Enable performance monitoring
    await page.evaluateOnNewDocument(() => {
        window.performanceMetrics = [];
        
        // Monitor all performance metrics
        new PerformanceObserver((list) => {
            for (const entry of list.getEntries()) {
                window.performanceMetrics.push({
                    name: entry.name,
                    type: entry.entryType,
                    duration: entry.duration,
                    timestamp: entry.startTime
                });
            }
        }).observe({ entryTypes: ['navigation', 'resource', 'paint', 'measure'] });
    });
    
    await page.goto('http://localhost:1313');
    
    // Get metrics
    const metrics = await page.evaluate(() => window.performanceMetrics);
    const performanceData = await page.metrics();
    
    // Save report
    await fs.writeFile('performance-report.json', JSON.stringify({
        timestamp: new Date().toISOString(),
        metrics,
        performance: performanceData
    }, null, 2));
    
    await browser.close();
    console.log('‚úÖ Performance report saved');
}

if (require.main === module) {
    monitorPerformance();
}

module.exports = { monitorPerformance };

